import React, { useState } from "react";
import "./scss/style.scss";

const fetchData = (message: string) => {
  fetch(
    `https://nethergames.de:1763/check?message=${message}&render=true`
  ).then((res) => {
    // decodeURl(res)
    console.log(res);
  });
};

const decodeURl = (responseUrl: string) => {
  console.log(responseUrl.substring(2, 4));
}

const App = () => {
  const [textAreaValue, setTextAreaValue] = useState<string>("");
  return (
    <div className="container">
      <div className="container__text">
        <textarea
          value={textAreaValue}
          onChange={(event: React.ChangeEvent<HTMLTextAreaElement>): void =>
            setTextAreaValue(event.currentTarget.value)
          }
          className="container__input"
        />
      </div>
      <div
        onClick={() => {
          fetchData(textAreaValue);
        }}
        className="container__button"
      >
        Button
      </div>
      <div
        className="container__output"
        dangerouslySetInnerHTML={{ __html: "message" }}
      />
    </div>
  );
};

export default App;
